    return (
        
        <div>
            {loading ? (
                <></>
         ) : (
            <div>
            {pokeData?.map((pokemon) => (
                <div>
        <li key={pokemon.id}><h1>{pokemon.name}</h1></li>
        <li><h2>{pokemon.abilities}</h2></li>
        <li><h3>{pokemon.game_indices}</h3></li>
        
        <li>
            <h4>
                {pokemon.types.map((types) => (
            <div>{types}</div>))}
            </h4></li>
        <li><h5>{pokemon.id}</h5></li>
        
