import { gql } from '@apollo/client';

export const QUERY_POKEMON = gql`
query pokemoninfo {
    pokemoninfo {
        _id
        abilities {
            name
        }
        game_indices {
            name
        }
        sprites 
        id
        name
        types {
            name
        }
    }
}
`
