import { QUERY_POKEMON } from '../utils/queries';
import React, { useState, useEffect } from 'react'

const Home = () => {
    const [pokeData, setPokeData] = useState(null);
    const {data, loading, error} = useQuery(QUERY_POKEMON);
    console.log(data)
    let pokemonDataArray = [];
    const pokemonData = data?.data?.pokemoninfo;
    pokemonDataArray.push(pokemonData)
    console.log(pokemonDataArray)
    // useEffect(() => {
    //     useQuery(QUERY_POKEMON)
    // })


    // pokemonData.forEach(element => pokemonDataArray.push(element))
    console.log(pokemonDataArray);
    console.log(typeof pokemonData)
    return (
        
        <div>
            {loading ? (
                <h1>Loading</h1>
         ) : (
            <div>
                {pokemonDataArray.map((pokemon) => (
            <li>{pokemon.name}</li>

        ))}
            </div>
         )}
            Help
        
        </div>
    )
}

export default Home
